INSERT INTO `l_question` VALUES (394, 'A', 30914, 3, NULL, NULL, '4＋2＝6分析：根据图意，有4个小朋友在捉蝴蝶，旁边有2个小朋友在赏花，所以图中一共有4＋2＝6个小朋友。<br />故选：A。本题主要考查整数的认识。', 2, '图中一共有（&nbsp;&nbsp;&nbsp;）个小朋友。 <br /><img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image006.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295857);
