INSERT INTO `l_question` VALUES (399, '【答案】8；2；100', 30914, 3, NULL, NULL, '【解析】80里面8个十，加2个十就是10个十，10个十是100个一，所以答案为8 ，2 ，100。<br />分析：数的计数单位和数的组成的考查，是数加减运算算理的基础。', 18, '【题文】80里面有________个十，再加________个十就是10个十，也就是________个一。', NULL, NULL, 295862);
INSERT INTO `l_question` VALUES (400, '【答案】74；100', 30914, 3, NULL, NULL, '【解析】4个一是4，7个十是70，合在一起是74，10个十就是100，所以答案为74和10020<br />分析：数的计数单位和数的组成的考查，是数加减运算算理的基础。', 18, '【题文】和4个一，7个十合起来是________，10个十是________。', NULL, NULL, 295863);
INSERT INTO `l_question` VALUES (401, '【答案】9；8；10', 30914, 3, NULL, NULL, '【解析】98中有9个十和8个一，一百中有10个十。<br />分析：本题考查学生的加减的正向思维和逆向思维，只要明白运算符号的含义，认真思考就能算出所填数值。', 18, '【题文】________个十和________个一是98；________个十是一百。', NULL, NULL, 295864);
INSERT INTO `l_question` VALUES (402, '【答案】5；2', 30914, 3, NULL, NULL, '【解析】从左数，第四张图上有5朵花，有3朵花的是第2张图。<br />分析：从左数，第四张图上有5朵花，有3朵花的是第2张图，即可解答。', 18, '【题文】从左数，第四张图上有________朵花，有3朵花的是第________张图。<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image011.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image012.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image013.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image014.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image015.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295865);
INSERT INTO `l_question` VALUES (403, '【答案】1(2)；4(3)', 30914, 3, NULL, NULL, '【解析】8＋5＝8＋1＋4或8＋5＝8＋2＋3<br />分析：根据已知，8＋5＝8＋□＋□，也就是等于号右边两个□里的数的和是5，根据分合法，5能分成1和4，5能分成2和3。<br />故答案为：1，4｜2，3。此题考查整数的认识。', 18, '【题文】8＋5＝8＋________＋________。', NULL, NULL, 295866);
INSERT INTO `l_question` VALUES (404, '【答案】5', 30914, 3, NULL, NULL, '【解析】3＋1＋1＝5（只）<br />分析：根据序数的意义，表示事物的次序，根据已知，鸭妈妈的左边有3只小鸭，右边有一只小鸭，也就是从左边数，鸭妈妈排第4位，鸭妈妈的右边带有一只。共5只。<br />故答案为：5。此题考查整数的认识 。', 18, '【题文】鸭妈妈带着一群小鸭去游泳，鸭妈妈的左边有3只小鸭，右边有1只小鸭。他们一共有<u>&shy;________</u>只鸭子。', NULL, NULL, 295867);
INSERT INTO `l_question` VALUES (405, '【答案】2；5；3', 30914, 3, NULL, NULL, '【解析】一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />有2只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />有5只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />有3只。<br />分析：根据基数的意义，表示物品数量的多少。一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />有2只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />有5只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />有3只。<br />故答案为： 2|5|3。此题考查整数的认识 。', 18, '【题文】看图填空。<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image007.jpg\" style=\"vertical-align:middle;\" /><br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />________只<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />________只<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />&nbsp;________只。', NULL, NULL, 295868);
