INSERT INTO `l_question` VALUES (415, 'C', 30915, 3, NULL, NULL, '4只小熊减去1只小熊，剩下3只小熊。4－1＝3 。<br />分析：根据图意，原来有4个小熊，走了1只，还剩下3只。所以算式应该是4－1＝3 。<br />故答案为：C。本题主要考查整数的认识。', 2, '下图表示（&nbsp;&nbsp;&nbsp;）。<br /><img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image005.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295858);
INSERT INTO `l_question` VALUES (416, 'C', 30915, 3, NULL, NULL, '另一个 <img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image003.png\" style=\"vertical-align:middle;\" />放3个。<br />分析：根据数的组成，4是由1和3组成的。<br />故选：C。本题主要考查整数的认识。', 2, '把 <img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image001.png\" style=\"vertical-align:middle;\" />放在<img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image002.png\" style=\"vertical-align:middle;\" />里，一个<img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image003.png\" style=\"vertical-align:middle;\" />里放1个，另一个 <img src=\"Upload/2017-08/24/84522715-9c5c-43db-acda-95ea706eaa22/paper.files/image003.png\" style=\"vertical-align:middle;\" />放（&nbsp;&nbsp;）个。E、5', NULL, NULL, 295859);
INSERT INTO `l_question` VALUES (417, 'D', 30915, 3, NULL, NULL, '用0、3、2这三个数字，组成的三位数中不正确的是032，故选D。分析：0是一个特殊的数，表示一个也没有，不能写在最高位上。', 2, '用0、3、2这三个数字，组成的三位数中不正确的是（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295860);
INSERT INTO `l_question` VALUES (418, 'D', 30915, 3, NULL, NULL, '十五最小，<br />故答案为：D<br />分析：按照数数的顺序，应该是十五、二十五、三十五、四十五，最小的是十五，即可解答。', 2, '在二十五、三十五、四十五、十五这四个数中，最小的一个是（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295861);
INSERT INTO `l_question` VALUES (419, '【答案】8；2；100', 30915, 3, NULL, NULL, '【解析】80里面8个十，加2个十就是10个十，10个十是100个一，所以答案为8 ，2 ，100。<br />分析：数的计数单位和数的组成的考查，是数加减运算算理的基础。', 18, '【题文】80里面有________个十，再加________个十就是10个十，也就是________个一。', NULL, NULL, 295862);
INSERT INTO `l_question` VALUES (420, '【答案】74；100', 30915, 3, NULL, NULL, '【解析】4个一是4，7个十是70，合在一起是74，10个十就是100，所以答案为74和10020<br />分析：数的计数单位和数的组成的考查，是数加减运算算理的基础。', 18, '【题文】和4个一，7个十合起来是________，10个十是________。', NULL, NULL, 295863);
INSERT INTO `l_question` VALUES (421, '【答案】9；8；10', 30915, 3, NULL, NULL, '【解析】98中有9个十和8个一，一百中有10个十。<br />分析：本题考查学生的加减的正向思维和逆向思维，只要明白运算符号的含义，认真思考就能算出所填数值。', 18, '【题文】________个十和________个一是98；________个十是一百。', NULL, NULL, 295864);
INSERT INTO `l_question` VALUES (422, '【答案】5；2', 30915, 3, NULL, NULL, '【解析】从左数，第四张图上有5朵花，有3朵花的是第2张图。<br />分析：从左数，第四张图上有5朵花，有3朵花的是第2张图，即可解答。', 18, '【题文】从左数，第四张图上有________朵花，有3朵花的是第________张图。<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image011.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image012.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image013.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image014.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image015.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295865);
INSERT INTO `l_question` VALUES (423, '【答案】1(2)；4(3)', 30915, 3, NULL, NULL, '【解析】8＋5＝8＋1＋4或8＋5＝8＋2＋3<br />分析：根据已知，8＋5＝8＋□＋□，也就是等于号右边两个□里的数的和是5，根据分合法，5能分成1和4，5能分成2和3。<br />故答案为：1，4｜2，3。此题考查整数的认识。', 18, '【题文】8＋5＝8＋________＋________。', NULL, NULL, 295866);
INSERT INTO `l_question` VALUES (424, '【答案】5', 30915, 3, NULL, NULL, '【解析】3＋1＋1＝5（只）<br />分析：根据序数的意义，表示事物的次序，根据已知，鸭妈妈的左边有3只小鸭，右边有一只小鸭，也就是从左边数，鸭妈妈排第4位，鸭妈妈的右边带有一只。共5只。<br />故答案为：5。此题考查整数的认识 。', 18, '【题文】鸭妈妈带着一群小鸭去游泳，鸭妈妈的左边有3只小鸭，右边有1只小鸭。他们一共有<u>&shy;________</u>只鸭子。', NULL, NULL, 295867);
INSERT INTO `l_question` VALUES (425, '【答案】2；5；3', 30915, 3, NULL, NULL, '【解析】一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />有2只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />有5只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />有3只。<br />分析：根据基数的意义，表示物品数量的多少。一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />有2只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />有5只；一个一个地数， <img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />有3只。<br />故答案为： 2|5|3。此题考查整数的认识 。', 18, '【题文】看图填空。<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image007.jpg\" style=\"vertical-align:middle;\" /><br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image008.png\" style=\"vertical-align:middle;\" />________只<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image009.png\" style=\"vertical-align:middle;\" />________只<br /><img src=\"Upload/2017-08/24/74bf9c4f-41cf-4556-8a50-fd67bc829e6c/paper.files/image010.png\" style=\"vertical-align:middle;\" />&nbsp;________只。', NULL, NULL, 295868);
INSERT INTO `l_question` VALUES (426, '【答案】14；13；12；11；10；9；8', 30915, 3, NULL, NULL, '【解析】本题要求按照一个一个数的方法，从大到小按顺序写出17后面各数，依次为17、16、15、14、13、12、11、10、9、8。考查知识点为整数的认识。<br />分析：本题要求按照一个一个数的方法，从大到小按顺序写出17后面各数，依次为17、16、15、14、13、12、11、10、9、8。本题考查整数的认识。', 18, '【题文】按顺序填数。<br /><table border=\"1\" cellpadding=\"0\" cellspacing=\"0\"><tr><td valign=\"top\" style=\"border:inset black 1.0pt;padding:.75pt .75pt .75pt .75pt\">17<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">16<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">15<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td><td valign=\"top\" style=\"border:inset black 1.0pt;border-left:none;padding:.75pt .75pt .75pt .75pt\">________<br /></td></tr></table>&nbsp;', NULL, NULL, 295869);
INSERT INTO `l_question` VALUES (427, 'B', 30915, 3, NULL, NULL, '2个十是20。<br />分析：一个十是10，二个十是20。<br />故选：B。本题主要考查整数的认识。', 2, '2个十是（&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295872);
INSERT INTO `l_question` VALUES (428, 'D', 30915, 3, NULL, NULL, '4＋6＝10<br />分析：根据数的组成，4和6组成10，所以4＋6＝10。<br />故选：D。本题是考查整数的认识。', 2, '4＋6＝（&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295873);
INSERT INTO `l_question` VALUES (429, 'A', 30915, 2, NULL, NULL, '1－1＝0<br />分析：1减1什么也没有了，根据0的意义，0表示什么也没有，所以1减1用0表示。<br />故选：A。本题是考查整数的认识。', 2, '1－1＝（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295874);
INSERT INTO `l_question` VALUES (430, '【答案】（1）＜<br />（2）＜<br />（3）＜', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】在 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image014.png\" style=\"vertical-align:middle;\" />里填上“＞”、“=”或“＜”。<br />(1)0 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image015.png\" style=\"vertical-align:middle;\" />1<br />(2)4 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image014.png\" style=\"vertical-align:middle;\" />8<br />(3)6 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image014.png\" style=\"vertical-align:middle;\" />9', NULL, NULL, 295799);
