INSERT INTO `l_question` VALUES (433, '【答案】2；3；4；3；2；2', 30916, 3, NULL, NULL, '【解析】解答：图一： 2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image007.png\" style=\"vertical-align:middle;\" />，3个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image008.png\" style=\"vertical-align:middle;\" />，2＜3；图二：4个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image009.png\" style=\"vertical-align:middle;\" />，3个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image010.png\" style=\"vertical-align:middle;\" />，4＞3；图三：2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image011.png\" style=\"vertical-align:middle;\" />，2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image012.png\" style=\"vertical-align:middle;\" />，2＝2。&nbsp;故答案为：图一：2、3，2＜3； 图二：4、3，4＞3；图三：2、 2, 2＝2。<br />分析：数一数，图一里有2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image007.png\" style=\"vertical-align:middle;\" />，3个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image008.png\" style=\"vertical-align:middle;\" />，2＜3；图二里有4个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image009.png\" style=\"vertical-align:middle;\" />，3个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image010.png\" style=\"vertical-align:middle;\" />，4＞3；图三里有2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image011.png\" style=\"vertical-align:middle;\" />，2个 <img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image012.png\" style=\"vertical-align:middle;\" />，2＝2；即可解答。', 18, '【题文】看图填数。<img src=\"Upload/2017-08/24/cff8870d-51af-4b5d-b824-85154552b576/paper.files/image006.jpg\" style=\"vertical-align:middle;\" /><br />________＜________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________＞________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________&nbsp;＝&nbsp;&nbsp; ________', NULL, NULL, 295802);
INSERT INTO `l_question` VALUES (434, '【答案】＜；＞；＜；＜；＞；＜；=；＞', 30916, 3, NULL, NULL, '【解析】1＜2 5＞3 6＜7 2＜6 3＞0 4＜5 7=\"7\" 7＞4<br />分析：根据数数的先后顺序，1、 2、 3、 4、 5、 6、 7，即可解答。', 18, '【题文】在横线上里填上＞、＜ 或 ＝ 。<br />1 ________ 2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 ________ 3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6 ________ 7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2 ________ 6<br />3 ________ 0&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4 ________ 5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7 ________ 7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7 ________ 4', NULL, NULL, 295803);
INSERT INTO `l_question` VALUES (435, '【答案】＞；＜；＜；＝；＝；＞', 30916, 3, NULL, NULL, '【解析】先计算出得数后与另一个进行大小的比较，如：17＋1＝18，18比16大，所有填上“＞”<br />分析：强化整数的加法和减法和大小比较的掌握', 18, '【题文】在横线上填上“＞”、“＜”、“＝”<br />17＋1________16&nbsp;&nbsp;&nbsp; 12________4＋9&nbsp;&nbsp;&nbsp; 9＋6________20－3<br />15－7________17－9&nbsp;&nbsp;&nbsp;&nbsp; 17＋0________17－0&nbsp;&nbsp;&nbsp; 20－2________18－2', NULL, NULL, 295804);
