INSERT INTO `l_question` VALUES (440, 'B', 30916, 3, NULL, NULL, ' 5＜8 3＞2 7＜8 ，所以只有0＜1是正确的。分析：因为5＜8 3＞2 7＜8 ，原题里这三个选项都是错误的，即可解答。', 2, '下面选项正确的是（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295809);
INSERT INTO `l_question` VALUES (441, 'D', 30916, 3, NULL, NULL, ' 比15大，比19小的数有16、 17、 18，故选D。<br />分析：比15大，比19小的数有16、 17、 18，不可能是19，即可解答。', 2, '一个数，它比15大，比19小，这个数不可能是（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295810);
INSERT INTO `l_question` VALUES (442, 'D', 30916, 3, NULL, NULL, '比6大，比10小，这个数在6和10之间，故选D。<br />分析：一个数，比6大，比10小，这个数在6和10之间，即可解答。', 2, '一个数，比6大，比10小，它不可能是（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 295811);
INSERT INTO `l_question` VALUES (443, '【答案】（1）＞<br />（2）＞&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】在 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image018.png\" style=\"vertical-align:middle;\" />里填上“＞”、“＜”或“=”&nbsp;&nbsp;&nbsp;<br />(1)9 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image018.png\" style=\"vertical-align:middle;\" />6＋2&nbsp;&nbsp;&nbsp;<br />(2)7 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image018.png\" style=\"vertical-align:middle;\" />10－4&nbsp;&nbsp;&nbsp;', NULL, NULL, 295815);
INSERT INTO `l_question` VALUES (444, '【答案】（1）＞<br />（2）＞&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】在 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />里填上“＞”、“＜”或“=”&nbsp;&nbsp;&nbsp;<br />(1)2＋3 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />4&nbsp;&nbsp;&nbsp;<br />(2)9－2 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />6&nbsp;&nbsp;&nbsp;', NULL, NULL, 295816);
