INSERT INTO `l_question` VALUES (446, '【答案】（1）＜<br />（2）＜<br />（3）=<br />（4）＞&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】在 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />里填上＞、＜或＝．&nbsp;&nbsp;&nbsp;<br />(1)7 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />8&nbsp;&nbsp;&nbsp;<br />(2)4 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />7&nbsp;&nbsp;&nbsp;<br />(3)6 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />6&nbsp;&nbsp;&nbsp;<br />(4)7 <img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image017.png\" style=\"vertical-align:middle;\" />3&nbsp;&nbsp;&nbsp;', NULL, NULL, 295818);
INSERT INTO `l_question` VALUES (447, '【答案】（1）<br /><img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image015.jpg\" style=\"vertical-align:middle;\" /><br />（2）<br /><img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image016.png\" style=\"vertical-align:middle;\" />', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】比一比．&nbsp;&nbsp;&nbsp;<br />(1)<img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image013.jpg\" style=\"vertical-align:middle;\" /><br />(2)<img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image014.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295819);
INSERT INTO `l_question` VALUES (448, '【答案】&lt;；&gt;；&lt;；&lt;；&gt;；&lt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30916, 3, NULL, NULL, '【解析】略', 18, '【题文】比大小。<br />26________27&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 80________79&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12________19&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />38________47&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 56________46&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 44________55&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', NULL, NULL, 295820);
INSERT INTO `l_question` VALUES (449, '【答案】10；10；0＜4＜7＜8＜9＜10＜12＜15＜16＜19&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30916, 3, NULL, NULL, '【解析】（1）通过数数可知一共有10张数字卡片。（2）数字最大的是19。（3）左起第4张是10。（4）这些数按从小到大的顺序排列是0＜4＜7＜8＜9＜10＜12＜15＜16＜19。<br />分析：（1）通过数数的方法、可知这里面一共有10张数字。（2）通过整数大小的比较方法：先看位数，位数多的数大；位数相同，从最高位看起，相同数位上的数大的那个数就大。可知这列数字最大的是19，也可将这列数字按照从大到小顺序排列为0＜4＜7＜8＜9＜10＜12＜15＜16＜19。本题考查整数大小的比较。&nbsp;&nbsp;&nbsp;', 18, '【题文】想一想。、<br /><img src=\"Upload/2017-08/24/daa43273-0653-4dfd-81cf-621c0c96a5aa/paper.files/image012.jpg\" style=\"vertical-align:middle;\" /><br />①一共有________张数字卡片。<br />②左起第4张是________。<br />③把这些数按从小到大的顺序排列。________。&nbsp;&nbsp;&nbsp;', NULL, NULL, 295821);
INSERT INTO `l_question` VALUES (450, 'B', 30917, 3, NULL, NULL, '根据左右关系的相对性，下楼的小朋友走的方向是向下的，相对于楼梯他们是靠右边走的。<br />分析：根据左右关系的相对性，相对于上楼的小朋友，下楼的小朋友面向前方位于右侧。<br />故选：B', 2, '下楼的小朋友是靠（&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295745);
INSERT INTO `l_question` VALUES (451, '【答案】4；3；③④①⑤；⑤②', 30917, 3, NULL, NULL, '【解析】（1）由车头的朝向可知，3号车排第一，往后依次是4号、1号、5号、2号。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，再从车头的朝向可知，3号车排第一，往后依次是4号、1号、5号、2号。本题考查位置。（2）由车头的朝向可知，3号车排第一，往后依次是④号、①号、⑤号、②号。所以2号车前面有③④①⑤四辆车，1号车后面有⑤②两辆车。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，再从车头的朝向可知，3号车排第一，往后依次是④号、①号、⑤号、②号。本题考查位置。', 18, '【题文】照样子填一填。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image014.png\" style=\"vertical-align:middle;\" /><br />①3号车在最前面，5号车排第________，1号车排第________。<br />②2号车前面有________，1号车后面有________。', NULL, NULL, 295734);
INSERT INTO `l_question` VALUES (452, '【答案】剧场站；少年宫站', 30917, 2, NULL, NULL, '【解析】由图中箭头可知，车行驶的方向是从图书馆开往展览馆，图书馆是第一站。剧场站在体育场的前一站，少年宫在体育场的后一站。分析：车是沿着箭头的方向行驶，离箭头越远的站台越靠后。由图可知图书馆是第一站，剧场站在体育馆的前一站，少年宫在体育馆的后一站。本题考查位置。', 18, '【题文】体育场的前一站是________，后一站是________。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image013.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295735);
INSERT INTO `l_question` VALUES (453, '【答案】上；下', 30917, 3, NULL, NULL, '【解析】由图可知，小汽车在动车的上面，动车在小汽车的下面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。由于动车需要抬头才能看到小汽车，所以小汽车在动车的上面，动车在小汽车的下面。本题考查位置。', 18, '【题文】在括号里填上“上”或“下”。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image012.jpg\" style=\"vertical-align:middle;\" /><br />小汽车在动车的________面。<br />动车在小汽车的________面。', NULL, NULL, 295736);
INSERT INTO `l_question` VALUES (454, '【答案】小马；钢琴；洋娃娃；篮球；机器人；小车；小熊；机器人；9', 30917, 3, NULL, NULL, '【解析】（1）小车左边是小马，右边是钢琴，上边是洋娃娃，下边是篮球。（2）篮球在机器人的右边，在小车的左边。小马的上边是小熊，下边是机器人。（3）上面一共有9种物品。<br />分析：以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由此可知，小车左边是小马，右边是钢琴，篮球在机器人的右边，在小车的左边。再者，需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，由此可知小马的上边是小熊，下边是机器人。本题考查位置。', 18, '【题文】根据图片回答问题<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image008.jpg\" style=\"vertical-align:middle;\" /><br />①&nbsp;<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image009.jpg\" style=\"vertical-align:middle;\" />左边是________，右边是________，上边是________，下边是________<br />②&nbsp;<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image010.jpg\" style=\"vertical-align:middle;\" />在________的右边，在________的左边。 <img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image011.jpg\" style=\"vertical-align:middle;\" />的上边是________，下边是________。<br />③上面一共有________种物品。', NULL, NULL, 295737);
INSERT INTO `l_question` VALUES (455, '【答案】17', 30917, 3, NULL, NULL, '【解析】8＋8＋1＝17。<br />分析：面向的那一面叫前面，背对的那一面叫后面。排队时，小丽前面有8人，后面有8人，加上小丽本人，一共有17人。本题考查位置。', 18, '【题文】排队时，小丽前面有8人，后面有8人，一共有________人。', NULL, NULL, 295738);
INSERT INTO `l_question` VALUES (456, '【答案】左；左；左', 30917, 3, NULL, NULL, '【解析】观察图片可知，小狗站在蛇的左边，米老鼠站在蜜蜂的左边，蝴蝶站在米老鼠的左边。考查知识点为位置。分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。此时，观察图片可知，小狗站在蛇的左边，米老鼠站在蜜蜂的左边，蝴蝶站在米老鼠的左边。本题考查位置。', 18, '【题文】看图回答问题：<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image007.jpg\" style=\"vertical-align:middle;\" /><br />小狗站在蛇的________边。<br />米老鼠站在蜜蜂的________边。<br />蝴蝶站在米老鼠的________边。', NULL, NULL, 295739);
INSERT INTO `l_question` VALUES (457, '【答案】左；右；左', 30917, 3, NULL, NULL, '【解析】观察图片可知，书本在闹钟的左边，闹钟在书本的右边，花盆在小猫的左边。<br />分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由图可知，书本在闹钟的左边，闹钟在书本的右边，花盆在小猫的左边。本题考查位置。', 18, '【题文】在括号里填上“左”或”右”。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image006.jpg\" style=\"vertical-align:middle;\" /><br />书本在闹钟的________边。<br />闹钟在书本的________边。<br />花盆在小猫的________边。', NULL, NULL, 295740);
INSERT INTO `l_question` VALUES (458, '【答案】小红；小兰', 30917, 3, NULL, NULL, '【解析】小红在最上面，小兰在最下面。<br />分析：由小英住在小兰楼上，可知小英在小兰上面，小兰在小英下面；再由小红住在小英楼上可知，小红在最上面，小兰在最下面。本题考查位置。', 18, '【题文】看图回答问题．<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image005.jpg\" style=\"vertical-align:middle;\" /><br />问：________在最上面。________在最下面', NULL, NULL, 295741);
INSERT INTO `l_question` VALUES (459, 'D', 30917, 3, NULL, NULL, '根据交通法规，右边通行的原则。上楼、下楼和在路上行走我们都应靠右边走。<br />分析：我国交通法规定，车辆、行人右侧通行。<br />故选：D。', 2, '上楼、下楼和在路上行走我们都应靠（&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295743);
INSERT INTO `l_question` VALUES (460, 'C', 30917, 3, NULL, NULL, '根据小狗与小老鼠关系的相对性，小狗位于小老鼠的前方。<br />分析：根据前后关系的相对性。小狗相对于老鼠位于前边。<br />故答案为：C。', 2, '小狗在小老鼠的（&nbsp;&nbsp;&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295744);
INSERT INTO `l_question` VALUES (461, 'D', 30917, 3, NULL, NULL, '根据小老鼠和小狗的关系的相对性，小老鼠相对于小狗，位于小狗的后面。<br />分析：根据前后关系的相对性。小老鼠相对于小狗位于后边。<br />故选：D。', 2, '小老鼠在小狗的（&nbsp;&nbsp;&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295746);
INSERT INTO `l_question` VALUES (462, 'A', 30917, 3, NULL, NULL, '根据小鸟和小狗关系的相对性，小鸟位于小狗的上方。<br />分析：根据上下关系的相对性，小鸟相对于小狗位于上方。<br />故选：A。', 2, '小鸟在小狗的（&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295747);
INSERT INTO `l_question` VALUES (463, 'B', 30917, 3, NULL, NULL, '根据左右关系的相对性，上楼的小朋友向上走，相对于下楼的小朋友，他们上告右边走的。<br />分析：根据左右关系的相对性，上楼的小朋友面向前方相对于下楼的小朋友位于右侧。<br />故答案为：B。', 2, '上楼的小朋友是靠（&nbsp;&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295748);
INSERT INTO `l_question` VALUES (464, '【答案】上', 30917, 2, NULL, NULL, '【解析】根据上下关系的相对性，红灯在黄灯的上面。<br />故答案为：上。<br />分析：根据上下关系的相对性，红灯相对于黄灯位于上面。', 18, '【题文】红灯在黄灯的<u>________</u>面<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image015.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295749);
INSERT INTO `l_question` VALUES (465, '【答案】前；后；前；前；小冬；小青', 30917, 3, NULL, NULL, '【解析】（1）由图可知，小冬跑在最前面，小利跑在最后面。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，小冬跑在最前面，小利跑在最后面。本题考查位置－－－前后。（2）由图可知小冬在小青的前面，小青跑在小利的前面。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，由图可知小冬在小青的前面，小青跑在小利的前面。本题考查位置。（3）由图可知，小利的前面是小青和小冬。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，小冬和小青都在小利的前面。本题考查位置。', 18, '【题文】看图回答问题：<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image014.png\" style=\"vertical-align:middle;\" /><br />①小冬跑在最________面，小利跑在最________面。<br />②小冬在小青的________面，小青在小利的________面。<br />③小利的前面是________和________。', NULL, NULL, 295750);
INSERT INTO `l_question` VALUES (466, '【答案】4；小丽；5', 30917, 3, NULL, NULL, '【解析】<br />（1）由题可知，小红排第一，从小红数起，小阳排在第4，第3个是小丽。（2）图中一共有5个人。<br />分析：通过数数可知，此题一共有5个人。其中，小红排第一，从小红数起，小阳排在第4，第3个是小丽。本题考查位置。', 18, '【题文】小红排第一，从小红数起，小阳排在第________，第3个是________。图中一共有________个人<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image013.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295751);
INSERT INTO `l_question` VALUES (467, '【答案】左；右；左。', 30917, 3, NULL, NULL, '【解析】观察图片可知，小花站在最左边，小红站在最右边，小阳站在小丽的左边。分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。观察图片可知，小花站在最左边，小红站在最右边，小阳站在小丽的左边。本题考查位置。', 18, '【题文】看图回答问题：<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image012.png\" style=\"vertical-align:middle;\" /><br />小花站在最________边。<br />小红站在最________边。<br />小阳站在小丽的________边。', NULL, NULL, 295752);
INSERT INTO `l_question` VALUES (468, '【答案】樵夫；桥的上面；桥；樵夫的下面', 30917, 3, NULL, NULL, '【解析】由图可知，樵夫在桥的上面，桥在樵夫的下面，水在桥的下面等。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。由此可知，樵夫在桥的上面，桥在樵夫的下面。本题为开放题，答案言之有理即可。本题考查位置。', 18, '【题文】①________在________上。<br />②________在________下。<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image011.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295753);
INSERT INTO `l_question` VALUES (469, '【答案】下；上', 30917, 3, NULL, NULL, '【解析】由图可知，松鼠在小鸟的下面，小鸟在松鼠的上面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，处在松鼠的位置，需要抬头才能看到小鸟，所以松鼠在小鸟的下面，小鸟在松鼠的上面。本题考查位置。', 18, '【题文】松鼠在小鸟的________面。<br />小鸟在松鼠的________面。<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image010.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295754);
INSERT INTO `l_question` VALUES (470, 'B', 30918, 3, NULL, NULL, '根据左右关系的相对性，下楼的小朋友走的方向是向下的，相对于楼梯他们是靠右边走的。<br />分析：根据左右关系的相对性，相对于上楼的小朋友，下楼的小朋友面向前方位于右侧。<br />故选：B', 2, '下楼的小朋友是靠（&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295745);
INSERT INTO `l_question` VALUES (471, '【答案】4；3；③④①⑤；⑤②', 30918, 3, NULL, NULL, '【解析】（1）由车头的朝向可知，3号车排第一，往后依次是4号、1号、5号、2号。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，再从车头的朝向可知，3号车排第一，往后依次是4号、1号、5号、2号。本题考查位置。（2）由车头的朝向可知，3号车排第一，往后依次是④号、①号、⑤号、②号。所以2号车前面有③④①⑤四辆车，1号车后面有⑤②两辆车。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，再从车头的朝向可知，3号车排第一，往后依次是④号、①号、⑤号、②号。本题考查位置。', 18, '【题文】照样子填一填。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image014.png\" style=\"vertical-align:middle;\" /><br />①3号车在最前面，5号车排第________，1号车排第________。<br />②2号车前面有________，1号车后面有________。', NULL, NULL, 295734);
INSERT INTO `l_question` VALUES (472, '【答案】剧场站；少年宫站', 30918, 2, NULL, NULL, '【解析】由图中箭头可知，车行驶的方向是从图书馆开往展览馆，图书馆是第一站。剧场站在体育场的前一站，少年宫在体育场的后一站。分析：车是沿着箭头的方向行驶，离箭头越远的站台越靠后。由图可知图书馆是第一站，剧场站在体育馆的前一站，少年宫在体育馆的后一站。本题考查位置。', 18, '【题文】体育场的前一站是________，后一站是________。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image013.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295735);
INSERT INTO `l_question` VALUES (473, '【答案】上；下', 30918, 3, NULL, NULL, '【解析】由图可知，小汽车在动车的上面，动车在小汽车的下面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。由于动车需要抬头才能看到小汽车，所以小汽车在动车的上面，动车在小汽车的下面。本题考查位置。', 18, '【题文】在括号里填上“上”或“下”。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image012.jpg\" style=\"vertical-align:middle;\" /><br />小汽车在动车的________面。<br />动车在小汽车的________面。', NULL, NULL, 295736);
INSERT INTO `l_question` VALUES (474, '【答案】小马；钢琴；洋娃娃；篮球；机器人；小车；小熊；机器人；9', 30918, 3, NULL, NULL, '【解析】（1）小车左边是小马，右边是钢琴，上边是洋娃娃，下边是篮球。（2）篮球在机器人的右边，在小车的左边。小马的上边是小熊，下边是机器人。（3）上面一共有9种物品。<br />分析：以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由此可知，小车左边是小马，右边是钢琴，篮球在机器人的右边，在小车的左边。再者，需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，由此可知小马的上边是小熊，下边是机器人。本题考查位置。', 18, '【题文】根据图片回答问题<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image008.jpg\" style=\"vertical-align:middle;\" /><br />①&nbsp;<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image009.jpg\" style=\"vertical-align:middle;\" />左边是________，右边是________，上边是________，下边是________<br />②&nbsp;<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image010.jpg\" style=\"vertical-align:middle;\" />在________的右边，在________的左边。 <img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image011.jpg\" style=\"vertical-align:middle;\" />的上边是________，下边是________。<br />③上面一共有________种物品。', NULL, NULL, 295737);
INSERT INTO `l_question` VALUES (475, '【答案】17', 30918, 3, NULL, NULL, '【解析】8＋8＋1＝17。<br />分析：面向的那一面叫前面，背对的那一面叫后面。排队时，小丽前面有8人，后面有8人，加上小丽本人，一共有17人。本题考查位置。', 18, '【题文】排队时，小丽前面有8人，后面有8人，一共有________人。', NULL, NULL, 295738);
INSERT INTO `l_question` VALUES (476, '【答案】左；左；左', 30918, 3, NULL, NULL, '【解析】观察图片可知，小狗站在蛇的左边，米老鼠站在蜜蜂的左边，蝴蝶站在米老鼠的左边。考查知识点为位置。分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。此时，观察图片可知，小狗站在蛇的左边，米老鼠站在蜜蜂的左边，蝴蝶站在米老鼠的左边。本题考查位置。', 18, '【题文】看图回答问题：<img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image007.jpg\" style=\"vertical-align:middle;\" /><br />小狗站在蛇的________边。<br />米老鼠站在蜜蜂的________边。<br />蝴蝶站在米老鼠的________边。', NULL, NULL, 295739);
INSERT INTO `l_question` VALUES (477, '【答案】左；右；左', 30918, 3, NULL, NULL, '【解析】观察图片可知，书本在闹钟的左边，闹钟在书本的右边，花盆在小猫的左边。<br />分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由图可知，书本在闹钟的左边，闹钟在书本的右边，花盆在小猫的左边。本题考查位置。', 18, '【题文】在括号里填上“左”或”右”。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image006.jpg\" style=\"vertical-align:middle;\" /><br />书本在闹钟的________边。<br />闹钟在书本的________边。<br />花盆在小猫的________边。', NULL, NULL, 295740);
INSERT INTO `l_question` VALUES (478, '【答案】小红；小兰', 30918, 3, NULL, NULL, '【解析】小红在最上面，小兰在最下面。<br />分析：由小英住在小兰楼上，可知小英在小兰上面，小兰在小英下面；再由小红住在小英楼上可知，小红在最上面，小兰在最下面。本题考查位置。', 18, '【题文】看图回答问题．<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image005.jpg\" style=\"vertical-align:middle;\" /><br />问：________在最上面。________在最下面', NULL, NULL, 295741);
INSERT INTO `l_question` VALUES (479, 'D', 30918, 3, NULL, NULL, '根据交通法规，右边通行的原则。上楼、下楼和在路上行走我们都应靠右边走。<br />分析：我国交通法规定，车辆、行人右侧通行。<br />故选：D。', 2, '上楼、下楼和在路上行走我们都应靠（&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295743);
INSERT INTO `l_question` VALUES (480, 'C', 30918, 3, NULL, NULL, '根据小狗与小老鼠关系的相对性，小狗位于小老鼠的前方。<br />分析：根据前后关系的相对性。小狗相对于老鼠位于前边。<br />故答案为：C。', 2, '小狗在小老鼠的（&nbsp;&nbsp;&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295744);
INSERT INTO `l_question` VALUES (481, 'D', 30918, 3, NULL, NULL, '根据小老鼠和小狗的关系的相对性，小老鼠相对于小狗，位于小狗的后面。<br />分析：根据前后关系的相对性。小老鼠相对于小狗位于后边。<br />故选：D。', 2, '小老鼠在小狗的（&nbsp;&nbsp;&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295746);
INSERT INTO `l_question` VALUES (482, 'A', 30918, 3, NULL, NULL, '根据小鸟和小狗关系的相对性，小鸟位于小狗的上方。<br />分析：根据上下关系的相对性，小鸟相对于小狗位于上方。<br />故选：A。', 2, '小鸟在小狗的（&nbsp;）面。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295747);
INSERT INTO `l_question` VALUES (483, 'B', 30918, 3, NULL, NULL, '根据左右关系的相对性，上楼的小朋友向上走，相对于下楼的小朋友，他们上告右边走的。<br />分析：根据左右关系的相对性，上楼的小朋友面向前方相对于下楼的小朋友位于右侧。<br />故答案为：B。', 2, '上楼的小朋友是靠（&nbsp;&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/067ebffd-7737-4dfc-b4d5-05b3bb86092e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295748);
INSERT INTO `l_question` VALUES (484, '【答案】上', 30918, 2, NULL, NULL, '【解析】根据上下关系的相对性，红灯在黄灯的上面。<br />故答案为：上。<br />分析：根据上下关系的相对性，红灯相对于黄灯位于上面。', 18, '【题文】红灯在黄灯的<u>________</u>面<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image015.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295749);
INSERT INTO `l_question` VALUES (485, '【答案】前；后；前；前；小冬；小青', 30918, 3, NULL, NULL, '【解析】（1）由图可知，小冬跑在最前面，小利跑在最后面。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，小冬跑在最前面，小利跑在最后面。本题考查位置－－－前后。（2）由图可知小冬在小青的前面，小青跑在小利的前面。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，由图可知小冬在小青的前面，小青跑在小利的前面。本题考查位置。（3）由图可知，小利的前面是小青和小冬。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，小冬和小青都在小利的前面。本题考查位置。', 18, '【题文】看图回答问题：<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image014.png\" style=\"vertical-align:middle;\" /><br />①小冬跑在最________面，小利跑在最________面。<br />②小冬在小青的________面，小青在小利的________面。<br />③小利的前面是________和________。', NULL, NULL, 295750);
INSERT INTO `l_question` VALUES (486, '【答案】4；小丽；5', 30918, 3, NULL, NULL, '【解析】<br />（1）由题可知，小红排第一，从小红数起，小阳排在第4，第3个是小丽。（2）图中一共有5个人。<br />分析：通过数数可知，此题一共有5个人。其中，小红排第一，从小红数起，小阳排在第4，第3个是小丽。本题考查位置。', 18, '【题文】小红排第一，从小红数起，小阳排在第________，第3个是________。图中一共有________个人<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image013.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295751);
INSERT INTO `l_question` VALUES (487, '【答案】左；右；左。', 30918, 3, NULL, NULL, '【解析】观察图片可知，小花站在最左边，小红站在最右边，小阳站在小丽的左边。分析：一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。观察图片可知，小花站在最左边，小红站在最右边，小阳站在小丽的左边。本题考查位置。', 18, '【题文】看图回答问题：<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image012.png\" style=\"vertical-align:middle;\" /><br />小花站在最________边。<br />小红站在最________边。<br />小阳站在小丽的________边。', NULL, NULL, 295752);
INSERT INTO `l_question` VALUES (488, '【答案】樵夫；桥的上面；桥；樵夫的下面', 30918, 3, NULL, NULL, '【解析】由图可知，樵夫在桥的上面，桥在樵夫的下面，水在桥的下面等。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。由此可知，樵夫在桥的上面，桥在樵夫的下面。本题为开放题，答案言之有理即可。本题考查位置。', 18, '【题文】①________在________上。<br />②________在________下。<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image011.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295753);
INSERT INTO `l_question` VALUES (489, '【答案】下；上', 30918, 3, NULL, NULL, '【解析】由图可知，松鼠在小鸟的下面，小鸟在松鼠的上面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，处在松鼠的位置，需要抬头才能看到小鸟，所以松鼠在小鸟的下面，小鸟在松鼠的上面。本题考查位置。', 18, '【题文】松鼠在小鸟的________面。<br />小鸟在松鼠的________面。<br /><img src=\"Upload/2017-08/24/7e559db4-3f8c-4e84-83dd-9f4cc9b80fb1/paper.files/image010.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295754);
INSERT INTO `l_question` VALUES (490, '【答案】圆柱；球；圆柱；长方体；6；2', 30919, 3, NULL, NULL, '【解析】 <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的上面是圆柱 ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的下面是球 ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的左面是圆柱 ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的右面是长方体。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image021.jpg\" style=\"vertical-align:middle;\" />的左边有6 个图形，右边有2个图形。<br />分析：根据物体的位置关系即可解答。', 18, '【题文】<img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的上面是________ ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的下面是________ ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的左面是________ ， <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />的右面是________ 。 <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image021.jpg\" style=\"vertical-align:middle;\" />的左边有________ 个图形，右边有________个图形。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image022.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295684);
INSERT INTO `l_question` VALUES (491, '【答案】3；右', 30919, 3, NULL, NULL, '【解析】从梨的左边第一个开始数起，一共有3种水果；葡萄在梨的右边。<br />分析：根据左右关系的相对性，从梨的左边第一个开始数起，一共有3种水果；葡萄在它的右边。<br />故答案为：3|右。', 18, '【题文】梨的左边有________种水果。葡萄在它的________边。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image019.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295685);
INSERT INTO `l_question` VALUES (492, '【答案】4；小丽；5', 30919, 3, NULL, NULL, '【解析】<br />（1）由题可知，小红排第一，从小红数起，小阳排在第4，第3个是小丽。（2）图中一共有5个人。<br />分析：通过数数可知，此题一共有5个人。其中，小红排第一，从小红数起，小阳排在第4，第3个是小丽。本题考查位置。', 18, '【题文】小红排第一，从小红数起，小阳排在第________，第3个是________。图中一共有________个人<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image018.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295686);
INSERT INTO `l_question` VALUES (493, '【答案】前；后', 30919, 3, NULL, NULL, '【解析】由图可知，小兔在小狗面向的那面，所以小兔在小狗的前面，小狗在兔子背对的那面，所以小狗在兔子的后面。<br />分析：由于面向的那一面叫前面，背对的那一面叫后面，小猫在小狗面向的那面，所以小猫在小狗的前面，小猫在兔子背对的那面，所以小猫在兔子的后面。本题考查位置。', 18, '【题文】小兔在小狗的________面，小狗在小兔的________面。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image017.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295687);
INSERT INTO `l_question` VALUES (494, '【答案】小红；小兰', 30919, 3, NULL, NULL, '【解析】小红在最上面，小兰在最下面。<br />分析：由小英住在小兰楼上，可知小英在小兰上面，小兰在小英下面；再由小红住在小英楼上可知，小红在最上面，小兰在最下面。本题考查位置。', 18, '【题文】看图回答问题．<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image016.jpg\" style=\"vertical-align:middle;\" /><br />问：________在最上面。________在最下面', NULL, NULL, 295688);
INSERT INTO `l_question` VALUES (495, '【答案】下；上', 30919, 3, NULL, NULL, '【解析】由图可知，松鼠在小鸟的下面，小鸟在松鼠的上面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，处在松鼠的位置，需要抬头才能看到小鸟，所以松鼠在小鸟的下面，小鸟在松鼠的上面。本题考查位置。', 18, '【题文】松鼠在小鸟的________面。<br />小鸟在松鼠的________面。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image015.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295689);
INSERT INTO `l_question` VALUES (496, '【答案】小马；钢琴；洋娃娃；篮球；机器人；小车；小熊；机器人；9', 30919, 3, NULL, NULL, '【解析】（1）小车左边是小马，右边是钢琴，上边是洋娃娃，下边是篮球。（2）篮球在机器人的右边，在小车的左边。小马的上边是小熊，下边是机器人。（3）上面一共有9种物品。<br />分析：以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由此可知，小车左边是小马，右边是钢琴，篮球在机器人的右边，在小车的左边。再者，需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体，由此可知小马的上边是小熊，下边是机器人。本题考查位置。', 18, '【题文】根据图片回答问题<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image011.jpg\" style=\"vertical-align:middle;\" /><br />①&nbsp;<img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image012.jpg\" style=\"vertical-align:middle;\" />左边是________，右边是________，上边是________，下边是________<br />②&nbsp;<img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image013.jpg\" style=\"vertical-align:middle;\" />在________的右边，在________的左边。 <img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image014.jpg\" style=\"vertical-align:middle;\" />的上边是________，下边是________。<br />③上面一共有________种物品。', NULL, NULL, 295690);
INSERT INTO `l_question` VALUES (497, '【答案】11；12；14；16；17；18；18；12', 30919, 3, NULL, NULL, '【解析】（1）11、12、14、16、17、18。（2）从右往左数，第三个数是18。（3）一共有12个数字。<br />分析：此题应以观察者的左右为参照，观察者的右手边为右边，左手边为左边。根据物体左右位置的确定和数数可知，从右往左数，第三个数是18，一共有12个数字。本题考查位置。', 18, '【题文】看图回答问题<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image010.jpg\" style=\"vertical-align:middle;\" /><br />①请把上面缺少的数填上________ ________ ________ ________&nbsp; ________ ________&nbsp;。<br />②从右往左数，第三个数是________；<br />③一共有________个数字。', NULL, NULL, 295691);
INSERT INTO `l_question` VALUES (498, '【答案】③④⑥；①②⑤', 30919, 3, NULL, NULL, '【解析】左手的号码是③④⑥ ，右手的号码是①②⑤。分析：由物体的左右位置关系，和自己的左右手的模拟尝试可知。左手的号码是③④⑥ ，右手的号码是①②⑤。本题考查位置。', 18, '【题文】你能分辨出左手和右手吗？<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image009.jpg\" style=\"vertical-align:middle;\" /><br />左手的号码是________&nbsp;， 右手的号码是 ________。', NULL, NULL, 295692);
INSERT INTO `l_question` VALUES (499, 'B', 30919, 3, NULL, NULL, '从左边数起，小花是第三个。<br />分析：根据左右关系的相对性，从左边数起，小花排第三个。<br />故选：B。', 2, '小花是左边数起的第（&nbsp;&nbsp;&nbsp;）个。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image007.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295694);
INSERT INTO `l_question` VALUES (500, 'A', 30919, 3, NULL, NULL, '从左侧起数，小马排在第4位。<br />分析：根据左右关系的相对性。从左侧数小马排第4位。<br />故选：A。', 2, '下面是一群小动物在一起休息。从左数起小马是第（&nbsp;&nbsp;&nbsp;）位。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image006.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295695);
INSERT INTO `l_question` VALUES (501, 'B', 30919, 3, NULL, NULL, '根据左右关系的相对性，下楼的小朋友走的方向是向下的，相对于楼梯他们是靠右边走的。<br />分析：根据左右关系的相对性，相对于上楼的小朋友，下楼的小朋友面向前方位于右侧。<br />故选：B', 2, '下楼的小朋友是靠（&nbsp;&nbsp;&nbsp;）边走。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image005.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295696);
INSERT INTO `l_question` VALUES (502, 'C', 30919, 3, NULL, NULL, '根据上下关系的相对性，已知玲玲、桐桐、宁宁家的阳台上都摆着花。所以玲玲、桐桐、宁宁家只可能是2、3、5楼。而已知玲玲住在桐桐楼下，桐桐住在宁宁楼下，可推断宁宁住的最高，玲玲住的最矮。所以桐桐家住在3楼。<br />分析：根据上下关系的相对性。玲玲住在桐桐楼下，桐桐住在宁宁楼下。所以在她们三个中，桐桐住在中间，而摆花的楼中中间的是3楼。<br />故选：C。', 2, '猜猜看<br />玲玲、桐桐、宁宁家的阳台上都摆着花。玲玲住在桐桐楼下，桐桐住在宁宁楼下。<br />桐桐家住在第（&nbsp;&nbsp;&nbsp;）层。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image004.png\" style=\"vertical-align:middle;\" />E、5<br />F、6', NULL, NULL, 295697);
INSERT INTO `l_question` VALUES (503, 'D', 30919, 3, NULL, NULL, '根据位置关系的相对性，小象在小狗、小兔、小牛三个小动物的后面，所以小象跑在最后面。<br />分析：根据前后关系的相对性。小象相对于小狗、小兔、小牛位于最后边。<br />故选：D。', 2, '小象跑在最（&nbsp;&nbsp;）面。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image003.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295698);
INSERT INTO `l_question` VALUES (504, 'A', 30919, 3, NULL, NULL, '根据小鸟和小狗关系的相对性，小鸟位于小狗的上方。<br />分析：根据上下关系的相对性，小鸟相对于小狗位于上方。<br />故选：A。', 2, '小鸟在小狗的（&nbsp;）面。<br /><img src=\"Upload/2017-08/24/0064224e-6034-4eb7-8b72-8b89a680a146/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295699);
INSERT INTO `l_question` VALUES (505, '【答案】7', 30919, 3, NULL, NULL, '【解析】根据已知，从右数大客车是第5辆，也就是大客车的右侧有4辆车。也就是从右边数起，到大客车一共有5辆车。而大客车的左边第一辆车开始数起，一共有2辆车，所以一共有7辆车。<br />分析：根据左右关系的相对性，大客车从右边数起是第5辆，而大客车的左侧还有2辆车，也就是一共有7辆车。<br />故答案为：7。', 18, '【题文】从右数大客车是第5辆，一共有<u>________</u>辆车。<br /><img src=\"Upload/2017-08/24/f72f7df4-096c-48d6-98f2-73ef0ada5cfc/paper.files/image024.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295700);
INSERT INTO `l_question` VALUES (506, '【答案】下', 30919, 3, NULL, NULL, '【解析】根据上下关系的相对性，绿灯在黄灯的下面。故答案为： 下。<br />分析：根据上下关系的相对性，绿灯相对于黄灯位于下面。', 18, '【题文】绿灯在黄灯的<u>________</u>面<br /><img src=\"Upload/2017-08/24/f72f7df4-096c-48d6-98f2-73ef0ada5cfc/paper.files/image023.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 295701);
INSERT INTO `l_question` VALUES (507, '【答案】2；3；3；4', 30919, 3, NULL, NULL, '【解析】由小朋友脸的朝向可知，左边的小男孩排在最前面依次往后数小兰前面有2个、小兰排在第3；小兰后面有2个，从后面往前数小兰排第4个。<br />分析：由小朋友脸的朝向可知，左边的小男孩排在最前面，小兰前面有2个、从前往后数小兰排在第3；小兰后面有2个，从后面往前数小兰排第4个。本题考查位置。', 18, '【题文】动脑筋、想一想。<br /><img src=\"Upload/2017-08/24/f72f7df4-096c-48d6-98f2-73ef0ada5cfc/paper.files/image022.png\" style=\"vertical-align:middle;\" /><br />①在上图中，小兰前面有________名小朋友，从前面数她排第________。<br />②小兰后面有________名小朋友，从后面数，她排第________个。', NULL, NULL, 295702);
INSERT INTO `l_question` VALUES (508, '【答案】白雪公主；猪八戒；猴子；米老鼠', 30919, 3, NULL, NULL, '【解析】解答观察图可知，（1）熊猫的上面是白雪公主，（2）米老鼠的下面是猪八戒。（3）白雪公主的右边是猴子。（4）猪八戒的上面是米老鼠。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。当处在熊猫的位置，往上抬头看可以看到白雪公主，所以熊猫的上面是白雪公主。同理可知米老鼠的下面是猪八戒，猪八戒的上面是米老鼠。对于左右而言一般以观察者的左右为参照，观察者的右手边为右边，左手边为左边。由此可知猪八戒的上面是米老鼠。本题考查位置。', 18, '【题文】看图回答问题：<br />①熊猫的上面是________。<br />②米老鼠的下面是________。<br />③白雪公主的右边是________。<br />④猪八戒的上面是________。<br /><img src=\"Upload/2017-08/24/f72f7df4-096c-48d6-98f2-73ef0ada5cfc/paper.files/image021.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295703);
INSERT INTO `l_question` VALUES (509, '【答案】上；下', 30919, 3, NULL, NULL, '【解析】由图可知，红灯在黄灯的<u>上</u>面，绿灯在黄灯的下面。<br />分析：需要抬头看的是处于上面的物体，需要低头看的是处于下面的物体。处在红灯的位置需要低头方能看到黄灯，由此可知，红灯在黄灯的<u>上</u>面。同理可知，绿灯在黄灯的下面。本题考查位置。', 18, '【题文】在横线上填上“上”或“下”。<br />红灯在黄灯的________ 面，绿灯在黄灯的________ 面。<br />红灯亮，汽车停，绿灯亮，汽车行。<br />汽车、行人靠马路的右边前进。<br />小朋友们，你们也要遵守交通规则啊！<br /><img src=\"Upload/2017-08/24/f72f7df4-096c-48d6-98f2-73ef0ada5cfc/paper.files/image020.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 295704);
INSERT INTO `l_question` VALUES (510, 'D', 30920, 3, NULL, NULL, '解：10－4－3＝3<br />分析：10减4得6，6减3得3。<br />故选：D。', 2, '10－4－3＝（&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 296253);
INSERT INTO `l_question` VALUES (511, 'B', 30920, 3, NULL, NULL, '解：现在两棵树上还是8只小鸟，故选：B。分析：两棵树上共有8只小鸟，3只小鸟从第一棵树上飞到第二棵树上，没有飞走，所以现在两棵树上还是8只小鸟，即可解答。', 2, '两棵树上共有8只小鸟，3只小鸟从第一棵树上飞到第二棵树上，现在两棵树上一共有（&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;）只小鸟。', NULL, NULL, 296251);
INSERT INTO `l_question` VALUES (512, '【答案】 85', 30920, 3, NULL, NULL, '【解析】解：个位9＋6＝15，写5向十位进一，十位3＋4＋1＝8，所以39＋46＝85。<br />分析：加法练习', 18, '【题文】一个加数是39，另一个加数是46，和是________。', NULL, NULL, 296230);
INSERT INTO `l_question` VALUES (513, '【答案】 ＜', 30920, 3, NULL, NULL, '【解析】45＋9＝54，65－8＝57，54＜57，所以45＋9＜65－8。<br />分析：整数的比较', 18, '【题文】45＋9________65－8。', NULL, NULL, 296231);
INSERT INTO `l_question` VALUES (514, '【答案】14；3；17', 30920, 3, NULL, NULL, '【解析】解：观察图片，由图片的部分和整体的关系可知，14＋3＝17，3＋14＝17。<br />分析：观察图片，图片由2部分组成，左边有3根小棒，右边有14根小棒，一共有17根小棒。从部分和整体的关系可知，左边的小棒的数量＋右边的小棒的数量＝共的小棒的数量。本题考查整数的加法和减法。', 18, '【题文】填一填<br /><img src=\"Upload/2017-09/04/d89d24bd-a57a-42c7-baeb-67ffec0a569d/paper.files/image002.jpg\" style=\"vertical-align:middle;\" /><br />________ ＋________ ＝________', NULL, NULL, 296232);
INSERT INTO `l_question` VALUES (515, '【答案】 77', 30920, 3, NULL, NULL, '【解析】解：47＋30=77，所以一共有77只兔<br />分析：白兔加上灰兔得到兔子的总数', 18, '【题文】白兔有47只，灰兔有30只，那么一共有________只兔子。', NULL, NULL, 296233);
INSERT INTO `l_question` VALUES (516, 'A', 30920, 3, NULL, NULL, '解：丁宇那两本给巧燕后剩8本，巧燕接受丁宇给的两本后共有12本，所有12－8＝4。 分析：通过实际生活中的例子，强化对整数运算的掌握。', 2, '丁宇和巧燕都有10本书，丁宇给巧燕2本，那么丁宇比巧燕少（&nbsp;）本。', NULL, NULL, 296237);
INSERT INTO `l_question` VALUES (517, 'A', 30920, 3, NULL, NULL, '解：改题答案并不唯一，将各个选项的答案代进原式进行可以验算，可得A选项为正确答案。<br />分析：考查加减法的意义。', 2, '下列计算正确的是（ ）。<br />3＋( )＜11 12＜( )＋5 ( )－1＜6', NULL, NULL, 296238);
