INSERT INTO `l_question` VALUES (519, '【答案】 29', 30920, 3, NULL, NULL, '【解析】解：个位8－9不够减，向十位借一，18－9＝9，十位4－1－1＝2，所以48－19＝29。 分析：退位减法练习。', 18, '【题文】48－19＝________。', NULL, NULL, 296249);
INSERT INTO `l_question` VALUES (520, '【答案】 38', 30920, 3, NULL, NULL, '【解析】解：小明借给了小红8本，又借给了小刚30本，一共借了30＋8=38本书<br />分析：将借给两个人的书的数目加起来，就是合计借出去的数目', 18, '【题文】小明借给了小红8本书，又借给了小刚30本书，合计小明借出去了________本书。', NULL, NULL, 296248);
