INSERT INTO `l_question` VALUES (523, '【答案】1.3元', 30920, 3, NULL, NULL, '【解析】解：要将两个物品的价格加起来，也就是0.8加上0.5等于1.3元<br />分析：0.8元和0.5元相加，等于1.3元', 18, '【题文】要买下图的物品一共需要________。<br /><img src=\"Upload/2017-09/04/18c5cec1-04da-40b0-aa55-25f7417c2fc4/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 296245);
INSERT INTO `l_question` VALUES (524, '【答案】 96', 30920, 3, NULL, NULL, '【解析】解：蝴蝶标本有64本，蜻蜓标本有32本，一共是64＋32＝96本。<br />分析：加法计算。', 18, '【题文】一个共有________本？<br /><img src=\"Upload/2017-09/04/18c5cec1-04da-40b0-aa55-25f7417c2fc4/paper.files/image002.png\" style=\"vertical-align:middle;\" /><br />64本 32本', NULL, NULL, 296244);
INSERT INTO `l_question` VALUES (525, '【答案】 6', 30920, 3, NULL, NULL, '【解析】解：15看成是10＋5，10－9＝1，1＋5＝6。所以15－9＝6<br />分析：拆减法', 18, '【题文】15－9＝________。', NULL, NULL, 296243);
INSERT INTO `l_question` VALUES (526, '【答案】6＋4＝10&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;', 30920, 3, NULL, NULL, '【解析】解：原来有6个，又买来4个，求总的个数，用加法，列式为6＋4＝10，所以选②。<br />分析：考查十以内的加法计算。&nbsp;&nbsp;&nbsp;', 18, '【题文】小明有6个苹果，妈妈又给他买了4个。求现在总的苹果数，应列式为________。&nbsp;&nbsp;&nbsp;', NULL, NULL, 296220);
INSERT INTO `l_question` VALUES (527, 'B', 30920, 3, NULL, NULL, '解答： <img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image002.png\" style=\"vertical-align:middle;\" />&nbsp;比 <img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image003.png\" style=\"vertical-align:middle;\" />&nbsp;多4个。<br />故选：B<br />分析:图中 <img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image002.png\" style=\"vertical-align:middle;\" />&nbsp;和 <img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image003.png\" style=\"vertical-align:middle;\" />&nbsp;是一一对应摆放的，苹果多出来的部分是4个，即可解答。', 2, '<img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image002.png\" style=\"vertical-align:middle;\" />&nbsp;比 <img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image003.png\" style=\"vertical-align:middle;\" />&nbsp;多（ ）个。<br /><img src=\"Upload/2017-09/04/72180253-b1dd-48f3-bcee-1d4677037b5c/paper.files/image004.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 296222);
