INSERT INTO `l_question` VALUES (530, '【答案】1；3；5', 30921, 2, NULL, NULL, '【解析】1、 3、 5。<br />分析：0后面是1,2后面是3,4后面是5，即可解答。', 18, '【题文】看尺子填数。<br /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image017.png\" style=\"vertical-align:middle;\" /><br />________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________', NULL, NULL, 296367);
INSERT INTO `l_question` VALUES (532, '【答案】1；3；5', 30921, 3, NULL, NULL, '【解析】根据数的顺序， 2的前面是1，2的后面是3，4的后面是5。<br />分析：根据数的顺序，1、2、3、4、5……，2的前面是1，2的后面是3，4的后面是5。<br />故答案为：1|3|5。此题考查整数的认识。', 18, '【题文】按顺序填数。<br />________2________4________', NULL, NULL, 296368);
