INSERT INTO `l_question` VALUES (534, '【答案】8；2', 30921, 2, NULL, NULL, '【解析】根据自然数的概念，比8多1是9多2是10', 18, '【题文】<img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image012.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image013.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image013.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image013.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image014.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image015.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image014.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image016.png\" style=\"vertical-align:middle;\" />有________个 <img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image015.png\" style=\"vertical-align:middle;\" />，再加________个就是10个。', NULL, NULL, 296370);
