INSERT INTO `l_question` VALUES (537, 'B', 30921, 2, NULL, NULL, '从左数， <img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image004.jpg\" style=\"vertical-align:middle;\" />&nbsp;排第3。<br />分析：根据序数的意义，表示事物的次序。从左数， <img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image004.jpg\" style=\"vertical-align:middle;\" />排第3。<br />故选：B。本题主要考查整数的认识。', 2, '从左数， <img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image004.jpg\" style=\"vertical-align:middle;\" />排第（&nbsp;&nbsp;）。<br /><img src=\"Upload/2017-08/30/5e734425-1c70-44d2-9276-c67c520b2d09/paper.files/image005.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 296375);
