INSERT INTO `l_question` VALUES (540, '【答案】52', 30921, 3, NULL, NULL, '【解析】十位上是5个珠子，个位是2个珠子，就是52。<br />分析：认识整数。', 18, '【题文】写作________。<br /><img src=\"Upload/2017-08/30/9f398a91-83d9-47c9-9aa7-f667ef00a701/paper.files/image017.png\" style=\"vertical-align:middle;\" />', NULL, NULL, 296378);
