INSERT INTO `l_question` VALUES (542, '【答案】4', 30921, 3, NULL, NULL, '【解析】2＋1＋1＝4（个）<br />分析：根据序数的意义，表示事物的次序。根据已知林林前面有2个人，后面有1个人，再加上林林这一排一共有4个人。<br />故答案为：4<br />此题考查整数的认识', 18, '【题文】<img src=\"Upload/2017-08/30/9f398a91-83d9-47c9-9aa7-f667ef00a701/paper.files/image014.png\" style=\"vertical-align:middle;\" />林林前面有2个人，后面有1个人，这一排一共有<u>________</u>个人。', NULL, NULL, 296380);
INSERT INTO `l_question` VALUES (543, '【答案】15', 30921, 3, NULL, NULL, '【解析】由整数的组成可知，个位上是5，十位上是1，这个数是15。<br />分析：个位上的数字表示几个一，十位上的数字表示几个十。1个十和5个一合起来的数是15（10＋5＝15）。本题考查整数的认识。', 18, '【题文】个位上是5，十位上是1，这个数是________。', NULL, NULL, 296381);
