INSERT INTO `l_question` VALUES (652, '【答案】400；60；2；1319', 30927, 3, NULL, NULL, '【解析】 462=\" 400\" + 60 + 2 1000+300+10+9= 1319<br />分析：462里面有4个百，6个十，2个一，所以462=\" 400\" + 60 + 2 ；1000里面有1个千，千位上是1；300里面有3个百，百位上是3；10里面有1个十，十位上是1；9里面有9个一，个位上是9；合在一起组成1319，所以1000+300+10+9= 1319', 18, '【题文】462=________+________+________&nbsp;&nbsp;&nbsp;&nbsp; 1000+300+10+9=________', NULL, NULL, 296138);
INSERT INTO `l_question` VALUES (653, '【答案】8；5；6', 30927, 3, NULL, NULL, '【解析】 856里有 8 个百， 5 个十， 6 个一组成的。<br />分析：856的百位上的数是8，表示8个百，十位上的数是5，表示5个十，个位上的数是6，表示6个一，即可解答。', 18, '【题文】856里有________个百，________个十，________个一组成的。', NULL, NULL, 296139);
INSERT INTO `l_question` VALUES (654, '【答案】16；18；19；20', 30927, 3, NULL, NULL, '【解析】由题可知，本题要求一个一个往后数，将15后面的数字依次写出来，分别为15、16、17、18、19、20。<br />分析：15和17之间相差2，而且中间只有一个空，显然这列数字是一个一个地往后数，15后面的数分别为15、16、17、18、19、20。本题考查整数的认识。', 18, '【题文】按顺序写数字。15________，17________，________，________。', NULL, NULL, 296140);
