INSERT INTO `l_question` VALUES (688, '【答案】6；4；5；5；3；3；1', 30928, 3, NULL, NULL, '【解析】 略', 18, '【题文】我会数。<br /><img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image005.png\" style=\"vertical-align:middle;\" /><br />有（&nbsp;&nbsp;）个长方形；（&nbsp;&nbsp;）个正方形；（&nbsp;&nbsp;）个三角形；（&nbsp;&nbsp;）个圆柱；（&nbsp;&nbsp;）个圆形；（&nbsp;&nbsp;）个正方体；（&nbsp;&nbsp;）个长方体。', NULL, NULL, 272321);
INSERT INTO `l_question` VALUES (689, '【答案】1．②⑤；2．⑥；3．⑧⑨；4．<img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image007.png\" style=\"vertical-align:middle;\" />；5．③⑦<img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image008.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image009.png\" style=\"vertical-align:middle;\" />；6．①<img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image010.png\" style=\"vertical-align:middle;\" />；7．⑩<img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image011.png\" style=\"vertical-align:middle;\" />；8．④<img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image012.png\" style=\"vertical-align:middle;\" />', 30928, 3, NULL, NULL, '【解析】 略', 18, '【题文】把下列图形的序号写在对应名称的括号里。<br /><img src=\"Upload/2016-12/20/1e0c143b-4d9d-41b1-a881-4fbf1acae68c/paper.files/image006.png\" style=\"vertical-align:middle;\" /><br />1．长方体：（&nbsp;&nbsp;&nbsp;）；2．正方体：（&nbsp;&nbsp;&nbsp;）；3．圆柱：（&nbsp;&nbsp;&nbsp;）；4．球：（&nbsp;&nbsp;&nbsp;）；<br />5．长方形：（&nbsp;&nbsp;&nbsp;）；6．正方形：（&nbsp;&nbsp;&nbsp;）；7．三角形：（&nbsp;&nbsp;&nbsp;）；8．圆：（&nbsp;&nbsp;&nbsp;）。', NULL, NULL, 272320);
INSERT INTO `l_question` VALUES (690, 'A', 30929, 3, NULL, NULL, '根据数的顺序，1的后面3的前面是2。分析：根据数的顺序，1、2、3、4、5……，排在1后面3前面的数是2。<br />故选：A。此题考查整数的认识。', 2, '选出 <img src=\"Upload/2017-09/06/251b327a-1a81-458f-b8e9-bd210d8e7e3e/paper.files/image001.jpg\" style=\"vertical-align:middle;\" />上的数。<br />1&nbsp;&nbsp;<img src=\"Upload/2017-09/06/251b327a-1a81-458f-b8e9-bd210d8e7e3e/paper.files/image002.jpg\" style=\"vertical-align:middle;\" />&nbsp;&nbsp;&nbsp;3', NULL, NULL, 297337);
