INSERT INTO `l_question` VALUES (887, '【答案】13；13；11；9；12；14；12；9；11；11；11；12', 30940, 3, NULL, NULL, '【解析】<br />分析：整数加法的计算方法有数数法、画圈法或凑十法等。数数法：例如7＋6，可以在7的后面往后数6个数字即可得13。画圈法：例如7＋6，可在纸上先画出7个圆圈，再在后面画上6个圆圈，数出一共画了几个圆圈。凑十法：例如7＋6可以将6拆成3和3，将7和3先合起来得10，最后将3和10加起来得13。其余算式同理可得结果。一般选择数数法，或拆数法较为简便。本题考查整数的加法和减法。', 18, '【题文】口算：<br />7＋6＝________ 6＋7＝________ 6＋5＝________ 4＋5＝________<br />5＋7＝________ 5＋9＝________ 9＋3＝________ 4＋5＝________<br />7＋4＝________ 4＋7＝________ 6＋5＝________ 6＋6＝________', NULL, NULL, 297047);
