INSERT INTO `l_question` VALUES (908, '【答案】解：6＋1＋10＝17（个）<br />答：丽丽排在第7个，一共有17个小朋友。', 30941, 3, NULL, NULL, '【解析】分析：根据已知，丽丽前面有6个人，可知丽丽排在第7个。已知丽丽前面有6个人，后面有10个人，还有丽丽，所以一共有6＋1＋10＝17个小朋友。', 20, '【题文】小朋友排队去公园，丽丽前面有6个人，后面有10个人。丽丽排在第几个？一共有几个小朋友去公园？', NULL, NULL, 297033);
INSERT INTO `l_question` VALUES (909, '【答案】 解：9＋5＝14（元）<br />答：买一个文具盒14元。', 30941, 3, NULL, NULL, '【解析】用钢笔的价钱加上贵的5元，就得到了买文具盒用的钱。<br />分析：本题考查知道价格低的，又知道贵出的钱数，求买贵的物品用的钱数。该问题应该用加法，容易解决。', 20, '【题文】算一算，比一比。<br />一只钢笔笔9元钱，一个文具盒比它贵了5元。买一个文具盒多少钱 ？', NULL, NULL, 297437);
INSERT INTO `l_question` VALUES (910, '【答案】 14', 30941, 3, NULL, NULL, '【解析】本题主要考察学生的列式以及计算能力，8＋6＝14', 18, '【题文】比8大6的数是________', NULL, NULL, 297034);
INSERT INTO `l_question` VALUES (911, '【答案】 4', 30941, 3, NULL, NULL, '【解析】13看成是10＋3，10－9＝1，1＋3＝4。所以13－9＝4。<br />分析：拆减法', 18, '【题文】13－9＝________。', NULL, NULL, 297035);
INSERT INTO `l_question` VALUES (912, '【答案】 12', 30941, 3, NULL, NULL, '【解析】5＋7＝12（把）<br />分析：本题考查的是进位加法，认真观察即可解答。', 18, '【题文】我有5把 <img src=\"Upload/2017-09/11/58e3474f-d3b7-4056-9500-d865aee91c05/paper.files/image014.jpg\" style=\"vertical-align:middle;\" />&nbsp;小丽有7把 <img src=\"Upload/2017-09/11/58e3474f-d3b7-4056-9500-d865aee91c05/paper.files/image014.jpg\" style=\"vertical-align:middle;\" />&nbsp;，我们俩一共有________ 把 <img src=\"Upload/2017-09/11/58e3474f-d3b7-4056-9500-d865aee91c05/paper.files/image015.jpg\" style=\"vertical-align:middle;\" />&nbsp;。', NULL, NULL, 297036);
INSERT INTO `l_question` VALUES (913, '【答案】 正确', 30941, 3, NULL, NULL, '【解析】36后面第五个数是36＋5＝41，是对的。<br />分析：考察对加法的认识', 39, '【题文】36后面第五个数是41。', NULL, NULL, 297037);
INSERT INTO `l_question` VALUES (914, '【答案】正确', 30941, 3, NULL, NULL, '【解析】个位3＋9就是12，十位6＋1就是7，63＋9就是72。<br />分析：加法计算', 39, '【题文】 63＋9＝72。', NULL, NULL, 297038);
INSERT INTO `l_question` VALUES (915, '【答案】错误', 30941, 3, NULL, NULL, '【解析】18个苹果加上2千克苹果等于20个苹果。此句话是错误的。分析：18个苹果和2千克苹果，单位不统一不可以直接相加减。要根据加法的含义进行加法计算和列式。', 39, '【题文】18个苹果加上2千克苹果等于20个苹果。', NULL, NULL, 297039);
INSERT INTO `l_question` VALUES (916, '【答案】 正确', 30941, 3, NULL, NULL, '【解析】妈妈买回来5个桃子，爸爸买回来6个桃子，他们一共买回来11个桃子。原题说法正确。 分析：妈妈买回来的加上爸爸买回来的就是一共买回来的。5＋6＝11（个）即可解答。', 39, '【题文】妈妈买回来5个桃子，爸爸买回来6个桃子，他们一共买回来11个桃子。', NULL, NULL, 297024);
INSERT INTO `l_question` VALUES (917, '【答案】 错误', 30941, 3, NULL, NULL, '【解析】一个加数是7，另一个加数是5，和是12。 分析：7加5的和应该是12。', 39, '【题文】一个加数是7，另一个加数是5，和是13。', NULL, NULL, 297012);
INSERT INTO `l_question` VALUES (918, '【答案】正确', 30941, 3, NULL, NULL, '【解析】解答：再画6个 <img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" />就和 <img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" />一样多。这句话是正确的。&nbsp;分析：根据自然数的概念。画的时候要先数好圆圈的个数是15个，有了9个 <img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" />再加上6个就是15个。', 39, '【题文】<img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image002.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" />&nbsp;&nbsp;&nbsp;<img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" /><br />再画6个 <img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image004.png\" style=\"vertical-align:middle;\" />就和 <img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image003.png\" style=\"vertical-align:middle;\" />一样多。', NULL, NULL, 297011);
INSERT INTO `l_question` VALUES (919, '【答案】正确', 30941, 3, NULL, NULL, '【解析】6再加上4就是10，10再加上1就是11，所以4＋1＝5，所以括号里边填5是正确的。<br />分析：加法变式', 39, '【题文】6＋（ ）＝11&nbsp;括号里填5。', NULL, NULL, 297010);
INSERT INTO `l_question` VALUES (920, '【答案】9；4；13', 30941, 3, NULL, NULL, '【解析】观察图片，由图片的部分和整体的关系可知9＋4＝13。<br />分析：观察图片，图片的一个圈圈里由2个部分组成，左边有9根铅笔，右边有4根铅笔，一共有13根铅笔。从部分和整体的关系可知，左边的铅笔的数量＋右边的铅笔的数量＝一共的铅笔的数量。本题考查整数的加法和减法。', 18, '【题文】看图列式计算。<br /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image005.jpg\" style=\"vertical-align:middle;\" /><br />________ ＋________ ＝________', NULL, NULL, 297009);
INSERT INTO `l_question` VALUES (921, '【答案】17；14；13；16', 30941, 3, NULL, NULL, '【解析】8＋9＝17，8＋6＝14，8＋5＝13，8＋8＝16。<br />分析： 20以内整数加减法的计算可以利用凑10法解决，重要的是找到加数里较大的那个数，将大数先凑十，再计算，比较简便。本题考查整数的加法和减法。', 18, '【题文】在方框里填数。<br /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image006.jpg\" style=\"vertical-align:middle;\" /><br />________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ________&nbsp;&nbsp;&nbsp;&nbsp; ________', NULL, NULL, 297008);
INSERT INTO `l_question` VALUES (922, '【答案】 解：6＋6＝12（个）<br />答：白兔比灰兔多拔12个萝卜。', 30941, 3, NULL, NULL, '【解析】本题主要考查的是整数加减法，最后两人的萝卜同样多，说明白兔拔的萝卜比灰兔多，多两份它给灰兔的数量，由此可以去求白兔比灰兔多拔几个萝卜。<br />分析：20以内的加法', 20, '【题文】两只小兔子拔萝卜。白兔拔了14个，给灰兔6个后，它们俩的萝卜就同样多。白兔比灰兔多拔几个萝卜？', NULL, NULL, 297007);
INSERT INTO `l_question` VALUES (923, '【答案】解：5＋8＝13个', 30941, 3, NULL, NULL, '【解析】分析：考察理解能力及加法基本运算。依题意将题目转化为简单加法运算即5＋8＝13个。', 20, '【题文】根据图片所示内容列出算式并计算出结果。<br /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image007.jpg\" style=\"vertical-align:middle;\" /><br />共有几个小丑？', NULL, NULL, 297006);
INSERT INTO `l_question` VALUES (924, '【答案】 解：9＋5＝14（元）<br />答：买一个文具盒14元。', 30941, 3, NULL, NULL, '【解析】用钢笔的价钱加上贵的5元，就得到了买文具盒用的钱。<br />分析：本题考查知道价格低的，又知道贵出的钱数，求买贵的物品用的钱数。该问题应该用加法，容易解决。', 20, '【题文】一只钢笔笔9元钱，一个文具盒比它贵了5元。买一个文具盒多少钱 ？', NULL, NULL, 297005);
INSERT INTO `l_question` VALUES (925, '【答案】解：4＋9＋6－4＝15（只）<br />答：体育室的球比原来多了15只。', 30941, 3, NULL, NULL, '【解析】分析：根据已知，体育室有4只球，第一次买来9只，第二次买来6只，现在体育室球的只数是：4＋9＋6＝19（只），现在比原来多的也就是19减原来球的只数4得15只。<br />本题主要是考查整数的认识', 20, '【题文】体育室有4只球，第一次买来9只，第二次买来6只，体育室的球比原来多了几只？', NULL, NULL, 297004);
INSERT INTO `l_question` VALUES (926, '【答案】解：<br /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image009.png\" style=\"vertical-align:middle;\" />', 30941, 3, NULL, NULL, '【解析】分析：细心计算每个算式的得数，此题可解。', 42, '【题文】把得数相同的用线连起来。<br /><img src=\"Upload/2017-09/11/4516b151-3bd9-493c-a888-b05eab748468/paper.files/image008.jpg\" style=\"vertical-align:middle;\" />', NULL, NULL, 297003);
